import PropTypes from 'prop-types';
import React, { useEffect, useState } from 'react';
import MyContext from './MyContext';

function Provider({ children }) {
  const [planets, setPlanets] = useState([]);
  const [filter, setFilter] = useState({
    byName: { name: '' },
    byNumericValues: [{ column: 'population', comparison: 'maior que', value: 0 }],
  });

  useEffect(() => {
    const url = 'https://swapi.dev/api/planets';
    async function fetchPlanets() {
      try {
        const request = await fetch(url);
        function filterButton() {
          const { results } = planets;
          const [{ column, comparison, value }] = filter.byNumericValues;
          const filteredPlanets = results.filter((planet) => {
            if (comparison === 'maior que') {
              return planet[column] > Number(value) || planet[column] === 'unknown';
            }
            if (comparison === 'menor que') return planet[column];
            return planet[column] === value;
          });
          return filteredPlanets;
        }
        const response = await request.json();

        response.results.map((planet) => delete planet.residents);
        setPlanets(response);
      } catch (error) {
        console.log(error);
      }
    }

    fetchPlanets();
  }, []);

  function searchPlanetByName({ target }) {
    setFilter({ ...filter, byName: { name: target.value } });
  }

  function filterNamePlanets() {
    if (!planets) return null;
    return filterButton().filter((planet) => (
      planet.name.includes(filter.byName.name)
    ));
  }

  function columnFilter({ target }) {
    setFilter({ ...filter,
      byNumericValues: [{ ...filter.byNumericValues[0], column: target.value }] });
  }

  function comparisonFilter({ target }) {
    setFilter({ ...filter,
      byNumericValues: [{ ...filter.byNumericValues[0], comparison: target.value }] });
  }

  function valueFilter({ target }) {
    setFilter({ ...filter,
      byNumericValues: [{ ...filter.byNumericValues[0], value: target.value }] });
  }


  const contextValue = {
    planets,
    filter,
    searchPlanetByName,
    filterNamePlanets,
    columnFilter,
    comparisonFilter,
    valueFilter,
    filterButton,
  };

  return (
    <MyContext.Provider value={ { ...contextValue } }>
      { children }
    </MyContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
