import PropTypes from 'prop-types';
import React, { useEffect, useState } from 'react';
import MyContext from './MyContext';

function Provider({ children }) {
  const [planets, setPlanets] = useState([]);
  const [filter, setFilter] = useState({
    byName: { name: '' },
    byNumericValues: [{ column: 'population', comparison: 'maior que', value: '' }],
  });

  useEffect(() => {
    const url = 'https://swapi.dev/api/planets';
    async function fetchPlanets() {
      try {
        const request = await fetch(url);
        const response = await request.json();

        response.results.map((planet) => delete planet.residents);
        setPlanets(response);
      } catch (error) {
        console.log(error);
      }
    }

    fetchPlanets();
  }, []);

  function searchPlanetByName({ target }) {
    setFilter({ ...filter, byName: { name: target.value } });
  }

  function filterNamePlanets() {
    if (!planets) return null;
    return planets.results.filter((planet) => (
      planet.name.includes(filter.byName.name)
    ));
  }

  function columnFilter({ target }) {
    setFilter({ ...filter,
      byNumericValues: [{ ...filter.byNumericValues[0], column: target.value }] });
  }

  function comparisonFilter({ target }) {
    setFilter({ ...filter,
      byNumericValues: [{ ...filter.byNumericValues[0], comparison: target.value }] });
  }

  const contextValue = {
    planets,
    filter,
    searchPlanetByName,
    filterNamePlanets,
    columnFilter,

  };

  return (
    <MyContext.Provider value={ { ...contextValue } }>
      { children }
    </MyContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
