import React, { useContext } from 'react';
import MyContext from '../context/MyContext';
import Loading from './Loading';
import title from '../services/title';

function Table() {
  const { planets,
    searchNamePlanet, searchPlanetByName } = useContext(MyContext);
  const { results } = planets;

  if (!results) return <Loading />;
  const headers = Object.keys(results[0]);
  const formatedHeaders = headers.map((header) => (
    title(header.replace('_', ' '))
  ));

  return (
    <div>

      <input
        type="text"
        placeholder="find a planet by name"
        onChange={ searchPlanetByName }
        value={ searchNamePlanet }
      />
      <table>
        <thead>
          <tr>
            { formatedHeaders.map((header, index) => (
              <th key={ index }>{ header }</th>
            ))}
          </tr>
        </thead>
        <tbody>
          { results.map((planet) => (
            <tr key={ planet.name }>
              <th>{ planet.name }</th>
              <th>{ planet.rotation_period }</th>
              <th>{ planet.orbital_period }</th>
              <th>{ planet.diameter }</th>
              <th>{ planet.climate }</th>
              <th>{ planet.gravity }</th>
              <th>{ planet.terrain }</th>
              <th>{ planet.surface_water }</th>
              <th>{ planet.population }</th>
              <th>{ planet.films }</th>
              <th>{ planet.created }</th>
              <th>{ planet.edited }</th>
              <th>{ planet.url }</th>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
