import React, { useContext } from 'react';
import MyContext from '../context/MyContext';
import Loading from './Loading';
import title from '../services/title';

function Table() {
  const { planets } = useContext(MyContext);
  const { results } = planets;

  if (!results) return <Loading />;
  const headers = Object.keys(results[0]);
  const formatedHeaders = headers.map((header) => (
    title(header.replace('_', ' '))
  ));

  return (
    <table>
      <thead>
        <tr>
          { formatedHeaders.map((header, index) => (
            <th key={ index }>{ header }</th>
          ))}
        </tr>
      </thead>
      <tbody>
        { results.map((planet) => (
          <tr key={ planet.name }>
            <th>{ planet.name }</th>
            <th>{ planet.rotation_period }</th>
            <th>{ planet.orbital_period }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
            <th>{ planet.name }</th>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default Table;
